public List<FileBlockLocation> f(byte[] value) throws IOException {
        checkNotClosed();
        Objects.requireNonNull(value);
        List<FileBlockLocation> filledBlocks = new ArrayList<>();
        int bytesWritten = 0;
        while (bytesWritten < value.length) {
            createFileIfNeeded();
            var blockToWrite = emptyBlocks.remove(emptyBlocks.size() - 1);
            int bytesToWrite = Math.min(value.length - bytesWritten, blockToWrite.size());
            try (RandomAccessFile fileToWrite = new RandomAccessFile(blockToWrite.fileName(), "rw")) {
            fileToWrite.seek(blockToWrite.offset());
            fileToWrite.write(value, bytesWritten, bytesToWrite);
            }

            if (blockToWrite.size() - bytesToWrite > 0) {
            emptyBlocks.add(
                new FileBlockLocation(blockToWrite.fileName(),
                    blockToWrite.offset() + bytesToWrite,
                    blockToWrite.size() - bytesToWrite));
            }
            filledBlocks.add(
                new FileBlockLocation(blockToWrite.fileName(), blockToWrite.offset(), bytesToWrite));
            bytesWritten += bytesToWrite;
        }
        return filledBlocks;
    },"writes the value to a file, returns the blocks in which the value was written to"

