    int32_t y,
    int32_t width,
    int32_t height
) {
    in.read(reinterpret_cast<char *>(&bitmap_file_header), HEADER_SIZE);
    in.read(reinterpret_cast<char *>(&bitmap_info), INFO_SIZE);
    if (x < 0 || y < 0 || width <= 0 || x + width > bitmap_info.bi_width ||
        height <= 0 || y + height > bitmap_info.bi_height) {
        throw invalid_requested_area();
    }
    if (bitmap_info.bi_size != INFO_SIZE) {
        throw invalid_header_size(bitmap_info.bi_size);
    }
    if (bitmap_info.bi_height <= 0) {
        throw non_positive_height(bitmap_info.bi_height);
    }
    if (bitmap_info.bi_bit_count != 24) {
        throw unexpected_number_of_bits_per_pixel(bitmap_info.bi_bit_count);
    }
    if (bitmap_info.bi_clr_used != 0) {
        throw color_palette_usage();
    }
    aligned_width = add_padding(bitmap_info.bi_width);
    // NOLINTNEXTLINE (bugprone-narrowing-conversions)
    data.resize(aligned_width * bitmap_info.bi_height);
    // NOLINTNEXTLINE (bugprone-narrowing-conversions)
    in.read(data.data(), aligned_width * bitmap_info.bi_height);
},Read image to array

void f(int32_t x, int32_t y, int32_t width, int32_t height) {
    if (width == bitmap_info.bi_width && height == bitmap_info.bi_height) {
        return;
    }
    temp_aligned_width = add_padding(width);
    // NOLINTNEXTLINE (bugprone-narrowing-conversions)
    temp.resize(temp_aligned_width * height, 0);
    int32_t new_row = 1;
    for (int32_t row = bitmap_info.bi_height - y;
         row > bitmap_info.bi_height - y - height; row--) {
        for (int32_t column = 0; column < 3 * width; column++) {
            temp[column + (height - new_row) * temp_aligned_width] =
                data[column + 3 * x + (row - 1) * aligned_width];
        }
        new_row++;
    }
    change_image_information(width, height);
},The function that cuts a part of the image

void f(int32_t width, int32_t height) {
