    // NOLINTNEXTLINE (bugprone-narrowing-conversions)
    temp.resize(height * temp_aligned_width, 0);
    for (int32_t row = 0; row < height; row++) {
        for (int32_t column = 0; column < width; column++) {
            temp[row * temp_aligned_width + column * 3 + 0] =
                data[column * aligned_width + (height - 1 - row) * 3 + 0];
