m &operator*=(m &lhs, const m &rhs) {
    m::check_dimension_match(lhs.columns(), rhs.rows());
    std::vector<std::vector<int>> product(
        lhs.rows(), std::vector<int>(rhs.columns())
    );
    for (int row = 0; row < lhs.rows(); row++) {
        for (int column = 0; column < rhs.columns(); column++) {
            unsigned int sum = 0;
            for (int i = 0; i < lhs.columns(); i++) {
                sum += lhs.m_m[row][i] * rhs.m_m[i][column];
            }
            product[row][column] = static_cast<int>(sum);
        }
    }
    lhs.m_m = product;
    return lhs;
},product of matrices

