 std::ifstream &operator>>(std::ifstream &file, m &m) {
    int rows = 0;
    int columns = 0;
    file >> rows >> columns;
    if (file.eof()) {
        throw invalid_file_format();
    }
    if (rows == 0 || columns == 0) {
        rows = columns = 0;
    }
    std::vector<std::vector<int>> new_m(rows, std::vector<int>(columns));
    for (auto &row : new_m) {
        for (auto &element : row) {
            file >> element;
            if (file.eof()) {
                throw invalid_file_format();
            }
        }
    }
    m.m_m = new_m;
    return file;
},Read the matrix from the file
